<script type="text/javascript">
    var appPic=document.getElementById('appPic');
    if(window.devicePixelRatio < 2){
        appPic.setAttribute('src',"images/app.png");
    } else {
        appPic.setAttribute('src',"images/app2x.png");
    }

    var imgComplete;
    if (document.readyState == "complete") { imgComplete = 1;appPic.style.opacity=1; }
    else {
        document.onreadystatechange = function () {
            if (document.readyState == "complete") { imgComplete = 1;appPic.style.opacity=1;}
        }
    }
    setTimeout(function () {
        if(imgComplete !== 1) {
            appPic.setAttribute('src', '');
            appPic.style.opacity=0;
        }
    },5000);

function isWeiXin(){
    var ua = window.navigator.userAgent.toLowerCase();
    if(ua.match(/MicroMessenger/i) == 'micromessenger'){
        return true;
    }else{
        return false;
    }
}
function isQQ(){
    var ua = window.navigator.userAgent.toLowerCase();
    if(ua.match(/QQBrowser/i)){
        return true;
    }else{
        return false;
    }
}
function isiOS(){
    var ua = window.navigator.userAgent.toLowerCase();
    if(ua.match(/iPhone|iPad|iPod|iOS/i)){
        return true;
    }else{
        return false;
    }
}
function isMiui(){
    var ua = window.navigator.userAgent.toLowerCase();
    if(ua.match(/MiuiBrowser/i)){
        return true;
    }else{
        return false;
    }
}

function iosDown(){
		if(isWeiXin()){
			document.getElementById("mCover").style.display="block";
		}else{
			if(isiOS()){
				if(isQQ()){
					document.getElementById("mCover").style.display="block";
				} else{
					window.location.href='itms-services_/index8a81.html?action=download-manifest&amp;url=https%3A%2F%2Fgate.io%2Fdocs%2Fgateio_manifest.plist';
				}
			}else{
				alert("Your device is not supported, Please use a iOS device to install gate.io App iPhone version")
			}
		}
}
function adrDown(){
	if(isWeiXin()){
		document.getElementById("mCover").style.display="block";
	}else{
		if(isiOS()){
			if(isMiui()){
				window.location.href='docs/gateio_app.apk';
			} else{
				alert("Your device is not supported, Please use a Android device to install gate.io App Android version")
			}
			}else{
				window.location.href='docs/gateio_app.apk';
			}
	}
}

