                $("#marketlist_container_usdt").css('display', 'block');
                base = 'USDT';
            } else if (td == 'BTC') {
                $("#marketlist_container_btc").css('display', 'block');
                base = 'BTC';
            } else if (td == 'ETH') {
                $("#marketlist_container_eth").css('display', 'block');
                base = 'ETH';
            } else if (td == 'NEW') {
                $("#marketlist_container_new").css('display', 'block');
                base = 'NEW';
            }
            /*else {
             $("#marketlist_container_new").css('display', 'block');
             base = 'BTC';
             }*/

        return false;
    });

	$('#marketSearch').keyup(function(e) {
		var needle = $(this).val();
		$.each($('.bizhong_en'),
		function() {
			var symbol = $(this).html();
			if (symbol.toLowerCase().indexOf(needle.toLowerCase()) == -1) {
				$('#' + $(this).attr('line_id')).hide();
			} else {
				$('#' + $(this).attr('line_id')).show();
				 if (e.keyCode == 13) {
					window.location = "/trade/"+symbol+"_"+base;
				}
			}
		});
	}).focus(function() {
        if ($(this).val() === 'Search...') {
            $(this).val('');
        }
    }).blur(function() {
        if ($(this).val() === '') {
            $(this).val('Search...');
        }
    });
});

